package EqualsOperator;

public class Equals_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i=7;
int j=-1;
int k=7;
if(i==j) {
	System.out.println("i and j are equal");
}else {
	System.out.println("i and j are not equal");
}
if(k==i) {
	System.out.println("i and k are equal");
}else {
	System.out.println("i and k are not equal");
}
	}

}
